-- table sucursal
DROP TABLE IF EXISTS `sucursal`;
CREATE TABLE sucursal(
	`id`	 		INT(11) NOT NULL AUTO_INCREMENT COMMENT 'Identificador unico - (valor secuencial)',
	`id_localidad`	INT(11) NOT NULL,
	`id_tipo_num1`	INT(11)     DEFAULT NULL COMMENT 'Tipo de num. teléfonico',
	`id_tipo_num2`	INT(11)     DEFAULT NULL COMMENT 'Tipo de num. teléfonico',
	`numero1`		VARCHAR(20) DEFAULT NULL COMMENT 'num. teléfonico',
	`extension1`	VARCHAR(5)  DEFAULT NULL COMMENT 'num. extensión',
	`numero2`		VARCHAR(20) DEFAULT NULL COMMENT 'num. teléfonico',
	`extension2`	VARCHAR(5)  DEFAULT NULL COMMENT 'num. extensión',

	`nombre` 		VARCHAR(50) COLLATE UTF8_UNICODE_CI NOT NULL UNIQUE COMMENT 'Sucursal -- (debe ser unico en toda la columna)',
	`direccion`		text not null,
	`codigo_postal` VARCHAR(10) DEFAULT NULL 	COMMENT 'Cogido Postal',
	`email`			VARCHAR(50) DEFAULT NULL,
	`fecha_registro` DATE DEFAULT  NULL    	 	  COMMENT 'Fecha de registro',
	`fecha_edicion`	DATE DEFAULT  NULL    	 	  COMMENT 'Fecha de ultima actualización',
	`activo`		BOOLEAN NOT NULL DEFAULT TRUE COMMENT '¿Sucursal activa?',

	primary key(`id`),
