	FOREIGN KEY (`id_tipo_num2`)  REFERENCES `tipo_numero_telefonico` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=UTF8_UNICODE_CI COMMENT='Esta tabla para sucursales / franquicias';

DROP TABLE IF EXISTS `usuario`;
