	`auth_key`	 			VARCHAR(255) NOT NULL 		UNIQUE COMMENT 'Código privado de identidad',
	`token`	 	 			VARCHAR(255) NOT NULL 	 	UNIQUE COMMENT 'Código de acceso temporal',

