	`seccion`				  VARCHAR(50)     NOT NULL 		      COMMENT 'Sección',
	`horario`			  	  TEXT            NULL 			      COMMENT 'Horario',
	`cantidad_meses`		  INT(2)          NOT NULL 		      COMMENT 'Cantidad de meses',
	`cantidad_clases`		  INT(2)          NOT NULL 		      COMMENT 'Cantidad de clases',
	`precio_cuota` 			  decimal(10,2)   NOT NULL 		      COMMENT 'Precio de cuotas',
	`precio_matricula` 		  decimal(10,2)   NOT NULL 		      COMMENT 'Precio de matricula',
	`nro_cupos` 			  INT(3)          NOT NULL 		      COMMENT 'cupos totales',
	`nro_disponibles` 		  INT(3)          DEFAULT NULL        COMMENT 'disponibles totales',
	`examen`				  VARCHAR(255)    DEFAULT NULL        COMMENT 'Archivo',
	`fecha_inicio` 			   DATE            NOT NULL           COMMENT 'Fecha de inicio',
	`fecha_inicio_evaluacion`  DATE            NOT NULL           COMMENT 'Fecha de evaluacion',
	`fecha_fin_evaluacion`	   DATE            NOT NULL           COMMENT 'Fecha maxima para entrega de evaluación',
	`fecha_fin` 			   DATE            NOT NULL           COMMENT 'Fecha de finalización',
	`status`				   INT(1)          NOT NULL DEFAULT 0 COMMENT '0 no inciada | 1 inciada (en curso) | 2 finalizada | 3 cancelada',

	PRIMARY KEY (`id`),
